(436, 118, 0, '<p><strong>Guru</strong></p>\r\n<p>Guruku . . .</p>\r\n<p>Kala mentari masih tertidur</p>\r\n<p>Kau terjang embun pagi</p>\r\n<p>Mengejar ketepatan waktu</p>\r\n<p>Memberi bekal ilmu anak didikmu</p>\r\n<p>Guruku . . .</p>\r\n<p>Sungguh besar pengabadianmu</p>\r\n<p>Jasa-jasamu yang luhur itu</p>\r\n<p>Tak akan kulupakan</p>\r\n<p>Sepanjang hayatku</p>\r\n<p> </p>\r\n<p> </p>\r\n<p>Pernyataan yang sesuai dengan puisi tersebut adalah . . . .</p>', 'Simulasi US/M', '<p>Kesungguhan anak dalam menuntut ilmu.</p>', '<p>Pujian atas jasa-jasa gurunya.</p>', '<p>Pengorbanan murid kepada guru yang dihormati.</p>', '<p>Perjuangan guru yang berangkat ke sekolah setiap hari.</p>', '', '', '', '2', '', '4', 'main'),
(437, 118, 0, '<p><strong>Guru</strong></p>\r\n<p>Guruku . . .</p>\r\n<p>Kala mentari masih tertidur</p>\r\n<p>Kau terjang embun pagi</p>\r\n<p>Mengejar ketepatan waktu</p>\r\n<p>Memberi bekal ilmu anak didikmu</p>\r\n<p>Guruku . . .</p>\r\n<p>Sungguh besar pengabadianmu</p>\r\n<p>Jasa-jasamu yang luhur itu</p>\r\n<p>Tak akan kulupakan</p>\r\n<p>Sepanjang hayatku</p>\r\n<p> </p>\r\n<p> </p>\r\n<p>Makna kata <em>hayat</em> dalam puisi tersebut adalah . . . .</p>', 'Simulasi US/M', '<p>Waktu</p>', '<p>Hidup</p>', '<p>Perjalanan</p>', '<p>Nasib</p>', '', '', '', '2', '', '2', 'main'),
(438, 118, 0, '<p> Bacalah petunjuk berikut!</p>\r\n<p><strong>Petunjuk Penggunaan Blender</strong></p>\r\n<p>1)    Tekan tombol on pada blender.</p>\r\n<p>2)    Masukkan buah yang sudah dipotong-potong.</p>\r\n<p>3)    Tekan tombol off pada blender apabila buah sudah halus.</p>\r\n<p>4)    Pastikan kabel blender terhubung dengan stopkontak.</p>\r\n<p>5)    Cabut kabel blender dari stopkontak setelah digunakan.</p>\r\n<p>Urutan petunjuk penggunaan blender yang tepat adalah . . . .</p>', 'Simulasi US/M', '<p>2-1-4-3-5</p>', '<p>2-4-1-3-5</p>', '<p>2-4-1-5-3</p>', '<p>2-5-1-3-4</p>', '', '', '', '2', '', '4', 'main'),
