(303, 303, '<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Perhatikan tabel berikut, Isilah Soal 1 &ndash; 10 dengan kententuan / syarat yang telah di dintentukan:</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><img src=\"https://kursusonline.org/image/materi/303/201901221616385c46df7613a2f.png\" style=\"height:365px; width:624px\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><img src=\"https://kursusonline.org/image/materi/303/201901221616385c46df7613cf5.png\" style=\"height:280px; width:624px\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Melihat angka paling depan dari NIP</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Melihat kolom golongan</span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika kolom golongan 1 maka A</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika kolom golongan 2 maka B</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika kolom golongan 3 maka C</span></span></li>\r\n</ol>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Perhatikan Syarat 1 dan Syarat 2</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Gaji Pokok = Gaji + Honor</span></span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Masa Kerja = Tutu Buku &ndash; Mulai masuk</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Note : Masa kerja di isi dengan satuan tahun</span></span></p>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Tunj. Nikah di dapat jika karyawan sudah menikah</span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika laki-laki mendapat&nbsp; 15% dari Gaji Pokok</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika Perempuan mendapat 10% dari Gaji Pokok</span></span></li>\r\n</ol>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Tunj. Anak di isi </span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Maksimal anak yang mendapatkan tunjangan adalah 2 anak</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Tiap anak akan mendapatkan 5% dari gaji pokok</span></span></li>\r\n</ol>\r\n\r\n<ol start=\"7\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Tunj. Masa Kerja diberikan dengan kententuan</span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika Masa kerja diatas 10 tahun, maka akan mendapatkan tunj. 15% dari Gaji Pokok</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika Masa Kerja di antara 5 tahun &ndash; 10 tahun, maka akan mendapatkan tunj. 10% dari Gaji Pokok</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika Masa Kerja di bawah 5 tahun, maka akan mendapatkan tunj. 5% dari Gaji Pokok</span></span></li>\r\n</ol>\r\n\r\n<ol start=\"8\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Penghasilan di dapat dari menjumlahkan</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Gaji Pokok + TUNJ. ANAK + TUNJ. NIKAH + TUNJ. MASA KERJA</span></span></p>\r\n\r\n<ol start=\"9\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Pajak akan dikenakan :</span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika pengahasilan lebih dari 700000, maka akan terkena pajak 2,5% dari total penghasilan</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika penghasilan dianatara 400000 &nbsp;sampai dengan 700000, maka akan terkena pajak 1% dari total penghasilan</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika penghasilan dibawah 400000, maka akan terkena pajak 0,5% dari total penghasilan</span></span></li>\r\n</ol>\r\n\r\n<ol start=\"10\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Total Pendapatan di dapatkan dengan cara</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Penghasilan - Pajak</span></span></p>\r\n', '', '1', 0, '2019-01-22', '15:06:00', ''),
(304, 304, '<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Perhatikan tabel berikut, Kolom yang kosong dengan kententuan / syarat yang telah di dintentukan:</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><img src=\"https://kursusonline.org/image/materi/304/201901221617055c46df9139db9.png\" style=\"height:312px; width:625px\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kolom Negara Tujuan ditentukan dari Tabel Kode dan Tarif Tour</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Tanggal Kembali ditentukan dari kolom Tanggal berangkat + Lama</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Total biaya ditentukan dari tabel Kode dan Tarif Tour dengan kententuan:</span></span></li>\r\n</ol>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Tarif tour per-paket hanya berlaku 4 hari</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Jika Peserta ingin perjalanan lebih lama, maka akan akan dikenakan tarif extra tiap harinnya</span></span></li>\r\n</ol>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Discount 25% akan diberikan jika lama perjalanan lebih dari 6 hari</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Biaya yang harus di bayar adalah</span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:36pt; margin-right:0cm\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Total Biaya &ndash; Discount</span></span></p>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Tabel Rekapitulasi diisi menggunakan fungsi filter, Sumif dan Countif</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Buatlah grafik dengan perbandingan Kode Tour dan Banyak Peserta &nbsp;</span></span></li>\r\n</ol>\r\n', '', '1', 0, '2019-01-22', '16:16:00', '');
