INSERT INTO `bank_soal` VALUES (236, 118, 0, '<p><strong>Perhatikan petunjuk pemakaian obat berikut!</strong></p>\r\n<p><strong>Aturan penggunaan obat sekali minum</strong></p>\r\n<p>Usia 1 – 6 th, ¼ tablet</p>\r\n<p>Usia 7 – 10 th, ½ tablet</p>\r\n<p>Usia 11 – 15 th, 1 tablet</p>\r\n<p> </p>\r\n<p>Dosis yang tepat untuk anak usia 12 tahun adalah . . . tablet sekali minum.</p>', 'Membaca Berbagai Tesk Nonsastra', '<p>1</p>', '<p>¾</p>', '<p>½</p>', '<p>¼</p>', '', '', '', '2', '', '1', 'main');
INSERT INTO `bank_soal` VALUES (237, 118, 0, '<p><strong>Perhatikan petunjuk pemakaian obat berikut!</strong></p>\r\n<p><strong>Dosis</strong></p>\r\n<p>0 – 5 tahun   : 2x sehari @ 1 sendok teh</p>\r\n<p>6 – 12 tahun : 2x sehari @ 1 sendok makan</p>\r\n<p>> 12 tahun    : 3 – 4 kali sehari @ 1 sendok makan</p>\r\n<p> </p>\r\n<p>Usia rusdi delapan tahun. Aturan penggunaan obat untuk Rudi adalah . . . .</p>', 'Membaca Berbagai Tesk Nonsastra', '<p>Sehari minum tiga kali, sekali minum dua sendok makan</p>', '<p>Sehari minum dua kali, sekali minum satu sendok makan</p>', '<p>Sehari minum tiga kali, sekali minum satu sendok teh</p>', '<p>Sehari minum dua kali, sekali minum satu sendok teh</p>', '', '', '', '2', '', '2', 'main');
INSERT INTO `bank_soal` VALUES (238, 118, 0, '<p><strong>Perhatikan petunjuk pemakaian obat batuk berikut!</strong></p>\r\n<p><strong>Aturan pakai           :</strong></p>\r\n<p>7 – 12 tahun : 2 x 2 sendok sesudah makan</p>\r\n<p>2 – 6 tahun   : 1 x 1 sendok sesudah makan</p>\r\n<p> </p>\r\n<p>Riki berusia 11 tahun. Ia sedang batuk. Penggunaan obat yang tepat adalah . . . .</p>\r\n<p> </p>', 'Membaca Berbagai Tesk Nonsastra', '<p>Diminum 2 kali sehari sesudah makan, setiap minum 2 sendok</p>', '<p>Diminum 1 kali sehari sesudah makan, setiap minum 1 sendok</p>', '<p>Diminum 2 kali sehari sesudah makan, setiap minum 1 sendok</p>', '<p>Diminum 1 kali sehari sesudah makan, setiap minum 2 sendok</p>', '', '', '', '2', '', '1', 'main');
INSERT INTO `bank_soal` VALUES (239, 118, 0, '<p><strong>Perhatikan petunjuk pemakaian obat demam berikut!</strong></p>\r\n<p><strong>Aturan penggunaan:</strong></p>\r\n<p>2 x 1 tablet per hari untuk usia 6 – 12 tahun</p>\r\n<p>2 x 2 tablet per hari untuk usia 12 – 18 tahun</p>\r\n<p>3 x 2 tablet per hari untuk usia > 18 tahun</p>\r\n<p> </p>\r\n<p>Vino sedang demam. Usia Vina 19 tahun. Petunjuk penggunaan ibat demam yang tepat untuk Vino adalah . . . .</p>', 'Membaca Berbagai Tesk Nonsastra', '<p>Dua kali sehari, satu tablet tiap minum</p>', '<p>Tiga kali sehari, dua tablet tiap minum</p>', '<p>Satu kali sehari, satu tablet tiap minum</p>', '<p>Dua kali sehari, dua tablet tiap minum</p>', '', '', '', '2', '', '2', 'main');
INSERT INTO `bank_soal` VALUES (240, 118, 0, '<p><strong>Perhatikan petunjuk pemakaian obat flu berikut!</strong></p>\r\n<p>Dewasa          : 3 kali sehari @ 2 tablet.</p>\r\n<p>Anak-anak      : 3 kali sehari @ 1 tablet</p>\r\n<p>Di bawah 2 tahun harus sesuai dengan resep dokter.</p>\r\n<p> </p>\r\n<p>Petunjuk penggunaan obat flu untuk anak-anak yang tepat adalah . . . .</p>', 'Membaca Berbagai Tesk Nonsastra', '<p>Diminum 2 kali sehari @ 2 tablet</p>', '<p>Diminum 3 kali sehari @ 1 tablet</p>', '<p>Diminum 2 kali sehari @ 1 tablet</p>', '<p>Diminum 3 kali sehari @ 2 tablet</p>', '', '', '', '2', '', '2', 'main');
INSERT INTO `bank_soal` VALUES (241, 118, 0, '<p><strong>Bacalah petunjuk penggunaan produk berikut!</strong></p>\r\n<p>Petunjuk oenggunaan Cat Semprot</p>\r\n<p>1. Semprotkan cat ke permukaan dengan jarak 30 cm.</p>\r\n<p>2. Lakukan penyemprotan sampai tiga kali dengan waktu 30 menit.</p>\r\n<p>3. Kocok cat semprot berulang-ulang selama dua menit.</p>\r\n<p>4. Bersihkan permukaan yang akan dicat dari air dan debu.</p>\r\n<p>5. Setelah penyemprotan yang ketiga, diamkan hasil semprotan sampai benar-benar kering.</p>\r\n<p>6. Lepaskan tutup boto cata semprot.</p>\r\n<p>Urutan petujnku penggunaan cata semprot yang tepat adalah . . . .</p>', 'Membaca Berbagai Tesk Nonsastra', '<p>2-1-4-3-6-5</p>', '<p>3-6-1-5-4-2</p>', '<p>4-3-6-1-2-5</p>', '<p>6-4-2-1-5-3</p>', '', '', '', '2', '', '3', 'main');
INSERT INTO `bank_soal` VALUES (242, 118, 0, '<p><strong>Bacalah petunjuk penggunaan produk berikut!</strong></p>\r\n<p><strong>Petunjuk penggunaan <em>Tape Recorder</em></strong></p>\r\n<p>1) Masukkan kaset yang akan diputar.</p>\r\n<p>2) Tekan tombol <em>open</em>.</p>\r\n<p>3) Tekan tombol</p>\r\n<p>4) Tekan tombol <em>stop</em> untuk mematikan.</p>\r\n<p>5) Putarlah volume sesuai keinginan.</p>\r\n<p>Urutan petunjuk penggunaan <em>tape recorder </em>yang tept adalah . . . .</p>', 'Membaca Berbagai Tesk Nonsastra', '<p>2-1-3-5-4</p>', '<p>2-1-4-5-3</p>', '<p>2-3-1-4-5</p>', '<p>3-2-1-5-4</p>', '', '', '', '2', '', '1', 'main');
INSERT INTO `bank_soal` VALUES (243, 118, 0, '<p><strong>Bacalah petunjuk berikut!</strong></p>\r\n<p>1) Pulsamu bertambah, kamu bias gunakan untuk menelepon.</p>\r\n<p>2) Cek pulsa untuk mengetahui jumlah pulsa.</p>\r\n<p>3) Masukkan kode <em>voucher.</em></p>\r\n<p>4) Gosok bagian berhologram.</p>\r\n<p>Cara mengisi pulsa dengan kartu <em>voucher</em> yang tepat adalah . . . .</p>', 'Membaca Berbagai Tesk Nonsastra', '<p>4-3-2-1</p>', '<p>4-2-1-3</p>', '<p>3-4-1-2</p>', '<p>3-1-2-4</p>', '', '', '', '2', '', '1', 'main');
INSERT INTO `bank_soal` VALUES (244, 118, 0, '<p><strong>Perhatikan petunjuk penggunaan produk berikut!</strong></p>\r\n<p>1) Semprotkan cairan pembersih monitor.</p>\r\n<p>2) Bersihkan monitor laptop menggunakan kain halus.</p>\r\n<p>3)Keringkan monitor dengan kain halus.</p>\r\n<p>4) Singkirkan debu dari monitor lapto dengan kuas.</p>\r\n<p>Urutan ptunjuk membersihkan monitor laptop dengan cairan pembersih yang tepat adalah . . . .</p>', 'Membaca Berbagai Tesk Nonsastra', '<p>2-3-4-1</p>', '<p>2-4-1-3</p>', '<p>4-2-1-3</p>', '<p>4-2-3-1</p>', '', '', '', '2', '', '3', 'main');
INSERT INTO `bank_soal` VALUES (245, 118, 0, '<p><strong>Perhatikan petunjuk produk berikut!</strong></p>\r\n<p>Petunjuk Penggunan <em>DVD Player</em></p>\r\n<p>1) Tekan tombol <em>open </em>pada <em>DVD Player</em></p>\r\n<p>2) Masukkan kaset</p>\r\n<p>3) Nyalaan televise dan tekan tombol <em>Power DVD Player</em>.</p>\r\n<p>4) Tekan tombol <em>Play </em>untuk memutar kaset <em>DVD</em>.</p>\r\n<p>5) Pindahkan saluran TV ke saluran AV.</p>\r\n<p>Urutan petunjuk penggunaan <em>DVD Player</em> yang tepat adalah . . . .</p>', 'Membaca Berbagai Tesk Nonsastra', '<p>5-3-4-1-2</p>', '<p>3-5-1-2-4</p>', '<p>3-2-4-5-1</p>', '<p>1-4-3-5-2</p>', '', '', '', '2', '', '2', 'main');
INSERT INTO `bank_soal` VALUES (246, 118, 0, '<p><strong>Perhatikan rubrik berikut!</strong></p>\r\n<p><strong>Hatiku Cerah Bila Tidak Ada Sampah</strong></p>\r\n<p>Apa jadinya jika masyarakat tidak memperhatikan pameo itu? Pencemaran sudah membahayakan sungai. Beberapa warga membuang sampah di sungai. Ada pula pabrik yang tidak membuat instalasi Pengolahan Air Limbah (IPAL) dan langsung mengalirkan limbah pabrik itu ke sungai. Arinya menjadi kotor. Ikan-ikan pun banyak yang mati.</p>\r\n<p> Kalimat saran yang tepat untuk rubric tersebut adalah . . . .</p>', 'Rubrik', '<p>Masyarakat harus menyadari pentingnya kebersohan sungai.</p>', '<p>Masyarakat tidakmenghiaraukan lingkungan.</p>', '<p>Kita semua hendaknya menjaga ebersihan lingkungan.</p>', '<p>Penegelola pabrik diberi pengarahan.</p>', '', '', '', '2', '', '1', 'main');
INSERT INTO `bank_soal` VALUES (247, 118, 0, '<p><strong>Perhatikan rubrik berikut!</strong></p>\r\n<p><strong>Hatiku Cerah Bila Tidak Ada Sampah</strong></p>\r\n<p>Apa jadinya jika masyarakat tidak memperhatikan pameo itu? Pencemaran sudah membahayakan sungai. Beberapa warga membuang sampah di sungai. Ada pula pabrik yang tidak membuat instalasi Pengolahan Air Limbah (IPAL) dan langsung mengalirkan limbah pabrik itu ke sungai. Arinya menjadi kotor. Ikan-ikan pun banyak yang mati.</p>\r\n<p> Kalimat saran yang tepat untuk rubric tersebut adalah . . . .</p>', 'Rubrik', '<p>Masyarakat harus menyadari pentingnya kebersohan sungai.</p>', '<p>Masyarakat tidakmenghiaraukan lingkungan.</p>', '<p>Kita semua hendaknya menjaga ebersihan lingkungan.</p>', '<p>Penegelola pabrik diberi pengarahan.</p>', '', '', '', '2', '', '1', 'main');
INSERT INTO `bank_soal` VALUES (248, 118, 0, '<p><strong>Bacalah rubrik khusus berikut!</strong></p>\r\n<p>Makanan berwarna biasanya menarik atau memabngkitakan selera. Kita perlu berhati-hati bila memilih makanan berwarna. Tidak semua makanan menggunakan zat pewarna aman bagi kesehatan. Justru, makanan semacam itu biasanya tidak terdaftar dalam Badan Pengawasan Obat dan Makanan (BPOM).</p>\r\n<p> Kalimat saran yang tepat untuk rubric tersebut adalah . . . .</p>', 'Rubrik', '<p>Tidak semua makanan berwarna aman bagi kesehatan.</p>', '<p>Makanan berwarna tidak terdaftar dalam BPOM.</p>', '<p>Makanan berwarna biasanya membangkitkan selera makan.</p>', '<p>Kita perlu berhati-hati ketika memilih makanan berwarna.</p>', '', '', '', '2', '', '4', 'main');
INSERT INTO `bank_soal` VALUES (249, 118, 0, '<p><strong>Bacalah rubrik khusus berikut!</strong></p>\r\n<p>Bahasa adalah penguat kerukunan hidup masyarakat, bangsa, dan Negara. Bahasa Indonesia harus menjadi pemersatu di antara beragam bahasa daerah. Bahasa menunjukkan jati diri yang dapat mempersatukan penuturnya dalam wujud kehidupan. Indonesia memiliki keberagaman bahasa daerah. Jika tidak keberagaman bahasa daerah. Jika tidak dikelola dengan baik, bias menjadi potensi ancaman perpecahan. Padahal, bahasa daerah memiliki kekerabatan yang menunjukkan adanya keterkaitan setiap daerah.</p>\r\n<p> Kalimat saran yang tepat untuk rubric tersebut adalah . . . .</p>', 'Rubrik', '<p>Bahasa daerah menunjukkan keterkaitan dengan budaya setiap daerah.</p>', '<p>Bahasa dapat menjadi pemersatu keberagaman budaya daerah di Indonesia</p>', '<p>Bahasa adalah identitas bangsa sehingga harus digunakan dengan baik dan benar.</p>', '<p>Bahasa Indonesia dan bahasa dearh harus dikelola dengan baik serta dilestarikan agar tidak punah.</p>', '', '', '', '2', '', '4', 'main');
INSERT INTO `bank_soal` VALUES (250, 118, 0, '<p><strong>Bacalah rubrik surat pembaca berikut!</strong></p>\r\n<p>Hai. <em>Gatot Kaca! </em>Namaku Meri. Aku suka baca majalah <em>Gatot Kaca</em>! Oh iya, aku punya usul, hadiah “kuis kegemaranku” ditambah jumlahnya, ya! Kalau kita menang kuis, hadiahnya dikirim ke mana? Itu saja pertanyaanku. Terima kasih, <em>Gatot Kaca</em>.</p>\r\n<p> Kalimat balasan surat pembaca tersebut yang tepat adalah . . . .</p>', '', '<p>Wah, usul yang bagus untuk masalah kuis, sementara akan kami bicarakan kepada pimpinan. Harap maklumi, ya.</p>', '<p>Hai, Meri! Terima kasih kamu sudah kirim surat sekaligus memberikan usul ke redaksi kami. Usul kamu akan kami pertimbangkan lagi, ya! Karena jujur saja kami tidak bias langsung memberikan keputusan atas kuis tersebut.</p>', '<p>Terima kasih Meri atas usulmu. Mengenai kuis, sebenarnya sedang kami bicarakan saat ini agar diperbanyak. Namun, sepertinya masih perlu banyak pertimbangan lagi.</p>', '<p>Wah, usul yang bagus, Meri! Oke, nanti hadiah kuisnya diperbanyak. Jika kamu menang, hadiah kuis akan dikirm ke rumahmu. Jadi, jangan lupa cantumkan alamat lengkapmu, ya!</p>', '', '', '', '2', '', '4', 'main');
INSERT INTO `bank_soal` VALUES (251, 118, 0, '<p><strong>Bacalah cuplikan pengumuman berikut!</strong></p>\r\n<p><strong>Pengumuman</strong></p>\r\n<p>Telah hilang sebuah dompet wanita merah atas nama Alamsyah yang beralamat Jalan Angkasa No. 14 Kemayoran, Jakarta Pusat. Siapa saja yang menemukan dompet tersebut, harap segera mengembalikan ke alamat di atas dan akan diberi imbalan sepantasnya.</p>\r\n<p> Isi pengumuman tersebut adalah . . . .</p>', 'Pengumuman', '<p>Imbauan menjaga dompet</p>', '<p>Berita ditemukan dompet</p>', '<p>Promosi harga dompet</p>', '<p>Berita kehilangan dompet</p>', '', '', '', '2', '', '4', 'main');
INSERT INTO `bank_soal` VALUES (252, 118, 0, '<p><strong>Bacalah cuplikan pengumuman berikut!</strong></p>\r\n<p>Diumumkan kepada warga RT 002 bahwa pada hari Minggu tanggal 24 Agustus 2014 akan diadakan kerja bakti lingkungan. Untuk itu, seluruh warga diharapkan berkumpul pukul 07.30 dan membawa alat kebersihan.</p>\r\n<p> </p>\r\n<p>Isi pengumuman tersebut adalah . . . .</p>', '', '<p>Perkumpulan warga RT 002 pada hari Minggu</p>', '<p>Ajakan membawa alat kebersihan untuk kerja bakti</p>', '<p>Pembatalan pelaksanaan kerja bakti di RT 002</p>', '<p>Ajakan melakukan kerja bakti di RT 002 pada hari Minggu</p>', '', '', '', '2', '', '4', 'main');
INSERT INTO `bank_soal` VALUES (253, 118, 0, '<p><strong>Bacalah pengumuman berikut!</strong></p>\r\n<p>Akan diadakan lomba mewarnai pada hari Jumat tanggal 8 agustus 2015. Lomba tersebut diadakan dalam rangka peringatan HUT sekolah yang ke-25.</p>\r\n<p> </p>\r\n<p>Perbaikan kalimat yang tercetak miring dalam pengumuman agar menjadi kalimat efektif adalah . . . .</p>', '', '<p>Akan diadakan Lomba Mewarnai pada Jumat, tanggal 8 Agustus 2015</p>', '<p>Akan diadakan Lomba Mewarnai pada Jumat, 8 Agustus 2015</p>', '<p>Akan diadakan Lomba Mewarnai pada hari jumat, tanggal 8 Agustus 2014</p>', '<p>Akan diadakan Lomba Mewarnai pada Jumat, tanggal 8 Agusts 2014</p>', '', '', '', '2', '', '2', 'main');
