INSERT INTO `bank_soal` VALUES (302, 118, 0, '<p xss=removed><strong>Bacalah puisi berikut!</strong></p>\r\n<p xss=removed><strong>Hutan Hijau Menawan</strong></p>\r\n<p xss=removed> </p>\r\n<p xss=removed>Doa untuk alamku</p>\r\n<p xss=removed>Awan putih yang indah</p>\r\n<p xss=removed>Hutan hijau yang menawan</p>\r\n<p xss=removed>Suara air bening gemericik mengalir</p>\r\n<p xss=removed> </p>\r\n<p xss=removed>Hutanku, pulauku, negeriku,</p>\r\n<p xss=removed>Mengapa kau kini?</p>\r\n<p xss=removed>Tak kulihat lagi awan putih</p>\r\n<p xss=removed>Tak kulihat lagi hutan yang hijau</p>\r\n<p xss=removed> </p>\r\n<p xss=removed>Bumiku, apakah ini tanda kehancuran</p>\r\n<p xss=removed>Tangan-tangan itu</p>\r\n<p xss=removed>Membiarkan hutan ditebangi</p>\r\n<p xss=removed> </p>\r\n<p xss=removed>Merenunglah, berpikirlah,</p>\r\n<p xss=removed>Hentikanlah</p>\r\n<p xss=removed>Sebelum bencana itu datang</p>\r\n<p xss=removed> </p>\r\n<p xss=removed>Isi puisi tersebut adalah . . . .</p>', 'Puisi', '<p>Anjuran untuk berdoa bagi keselamatan lingkungan</p>', '<p>Ajakan untuk mencegah perusakan alam</p>', '<p>Membiarkan polusi menutup awan</p>', '<p>Membiarkan hutan ditabangi</p>', '', '', '', '2', '', '2', 'main');
