INSERT INTO `bank_soal` VALUES (358, 118, 0, '<p><strong>Perhatikan cuplikan pengumuman berikut!</strong></p>\r\n<p><strong>Pengumuman</strong></p>\r\n<p>Sehubung dengan hasil ulangan tengah semester ganjil. SD Bintaro mengadakan remedial mata pelajaran Bahasa Indonesia. Siswa yang mendapat nilai di bawah Kriteria Ketuntasan Minimal (KKM) harus . . . remedial. Atas perhatiannya, diucapkan terima kasih.</p>\r\n<p> </p>\r\n<p>Kata yang tepat untuk melengkapi pengumuman tersebut adalah . . . .</p>', 'Pengumuman', '<p>Mengabaikan</p>', '<p>Mematuhi</p>', '<p>Mengikuti</p>', '<p>Mengharapkan</p>', '', '', '', '2', '', '3', 'main');
INSERT INTO `bank_soal` VALUES (359, 118, 0, '<p><strong>Perhatikan cuplikan pengumuman berikut!</strong></p>\r\n<p><strong>Pengumuman</strong></p>\r\n<p>Dalam rangka . . . Hari Pendidikan Nasional, sekolah akan mengadakan lomba cerdas cermat. Seluruh perwakilan kelas diharapkan medaftar diri kepada panitia.</p>\r\n<p> </p>\r\n<p>Kata yang tepat untuk melengkapi pengumuman tersebut adalah . . . .</p>', 'Pengumuman', '<p>Menyukseskan</p>', '<p>Memelopori</p>', '<p>Memperingati</p>\r\n<p> </p>', '<p>Menumbuhkan</p>', '', '', '', '2', '', '3', 'main');
INSERT INTO `bank_soal` VALUES (360, 118, 0, '<p><strong>Perhatikan cuplikan pengumuman berikut!</strong></p>\r\n<p><strong>Pengumuman</strong></p>\r\n<p>Sekolah Sepak Bila (SSB) Tegar akan . . . pertandingan persahabatan dengan SSB Gesit pada hari Sabtu, 4 Mei 2013 di lapangan Kelurahan Mulia. Oleh karena itu, siswa SSB Tegar harus mempersiapkan diri.</p>\r\n<p> </p>\r\n<p>Kata yang tepat untuk melengkapi pengumuman tersebut adalah . . .</p>', 'Pengumuman', '<p>Diadakan</p>', '<p>Pengadaan</p>', '<p>Mengadakan</p>', '<p>Berada</p>', '', '', '', '2', '', '3', 'main');
INSERT INTO `bank_soal` VALUES (361, 118, 0, '<p><strong>Perhatikan cuplikan pengumuman berikut!</strong></p>\r\n<p>Diumumkan kepada seluruh siswa SDN Darussalam bahwa kegiatan keagamaan pagi akan dilaksanakan setiap Jumat, mulai Oktober 2013.</p>\r\n<p>. . . Oleh karena itu, semua siswa diharapkan membawa alat beribadah.</p>\r\n<p> </p>\r\n<p>Kata yang tepat untuk melengkapi pengumuman tersebut adalah . . . .</p>', 'Pengumuman', '<p>Kegiatan tersebut dilakukan sebelum jam pelajaran.</p>', '<p>Kegiatan ini harus dilakukan dengan sungguh-sungguh.</p>', '<p>Kegiatan tersebut akan dilaksanakan pada jam pelajaran pertama di tempat ibadah masing-masing yang telah disediakan.</p>', '<p>Kegiatan keagamaan akan dimulai setelah semua siswaw siap.</p>', '', '', '', '2', '', '3', 'main');
INSERT INTO `bank_soal` VALUES (362, 118, 0, '<p><strong>Perhatikan cuplikan pengumuman berikut!</strong></p>\r\n<p>. . . Panitia Peringatan Hari Pendidikan Nasional SDN Sejahtera 05 akan mengadakan lomba membuat puisi dan cerpen bertemakan pendidikan. Lomba akan diselenggarakan pada 6 Mei 2014.</p>\r\n<p> </p>\r\n<p>Kata yang tepat untuk melengkapi pengumuman tersebut adalah . . . .</p>', 'Pengumuman', '<p>Dalam rangka memperingati Hari Pendidikan Nasional.</p>', '<p>Bagi siswa yang ingin mengikuti lomba harap mengambil formulir di ruang guru,</p>', '<p>Untuk memeriahkan HUT SDN Sejahtera 05.</p>', '<p>SDN Sjahtera 05 akan mengadakan acara menarik.</p>', '', '', '', '2', '', '1', 'main');
INSERT INTO `bank_soal` VALUES (363, 118, 0, '<p><strong>Bacalah dialog berikut!</strong></p>\r\n<p>Mira               : “mengapa warung Ibu tutup kemarin?”</p>\r\n<p>Ibu Kantin    : “Iya, karena Ibu pergi ke rumah, cucu.”</p>\r\n<p>Mira               : “. . .”</p>\r\n<p>Ibu Kantin    : “<em>Alhamdulillah</em>, cuci Ibu sehat dan bertambah lucu.”</p>\r\n<p> </p>\r\n<p>Kalimat yang tepat untuk melengkapi dialog tersebut adalah . . . .</p>', 'Melengkapi Dialog', '<p>Di mana cucu Ibu tinggal?</p>', '<p>Apakah Ibu sekarang sudah lega?</p>', '<p>Mengapa ibu pergi ke rumah cucu?</p>', '<p>Bagaimana keadaan cucu Ibu?</p>', '', '', '', '2', '', '4', 'main');
INSERT INTO `bank_soal` VALUES (364, 118, 0, '<p><strong>Bacalah dialog berikut!</strong></p>\r\n<p>Wulan            : “Mengapa toko roti bakar Bapak tutup kemarin?”</p>\r\n<p>Pak Darma   : “Iya, karena kemari Bapak sakit.”</p>\r\n<p>Wulan            : “. . .”</p>\r\n<p>Pak Darma   : “<em>Alhamdulillah</em>, sekarang Bapak sudah sehat.”</p>\r\n<p><em> </em></p>\r\n<p>Kalimat yang tepat untuk melengkapi dialog tersebut adalah . . . .</p>', 'Melengkapi Dialog', '<p>Di mana Bapak dirawat?</p>', '<p>Bagaimana keadaan Bapak sekarang?</p>', '<p>Mengapa Bapak sehat?</p>', '<p>Apakah Bapak sudah minum obat?</p>', '', '', '', '2', '', '2', 'main');
INSERT INTO `bank_soal` VALUES (365, 118, 0, '<p><strong>Bacalah dialog berikut!</strong></p>\r\n<p>Ayah              : “Susi tolong belikan Ayah lem kayu, ya.”</p>\r\n<p>Susi                 : “. . .”</p>\r\n<p>Ayah              : “Ini uangnya. Terima kasih, ya. Warung di depan jalan tidak ada.”</p>\r\n<p>Susi                 : “Iya, Yah. Aku coba cari di dekat pasar.”</p>\r\n<p> </p>\r\n<p>Kalimat yang tepat untuk melengkapi dialog tersebut adalah . . . .</p>', 'Melengkapi Dialog', '<p>Aku tidak mau, Ayah</p>', '<p>Aku sibuk banyak tugas, Yah.</p>\r\n<p> </p>', '<p>Adik saja yang membelinya.</p>', '<p>Baiklah Ayah, akan aku belikan.</p>', '', '', '', '2', '', '4', 'main');
INSERT INTO `bank_soal` VALUES (366, 118, 0, '<p><strong>Bacalah dialog berikut!</strong></p>\r\n<p>Nana              : “. . .”</p>\r\n<p>Ibu                  : “Ibu sedang memberi pupuk untuk tanaman hias.”</p>\r\n<p>Nana              : “Aku boleh membantu, Bu?”</p>\r\n<p>Ibu                  : “Tentu saja. Sebelum membantu, kamu pakai sarung tangan dulu agar tidak kotor.”</p>\r\n<p> </p>\r\n<p>Kalimat yang tepat untuk melengkapi dialog tersebut adalah . . . .</p>', 'Melengkapi Dialog', '<p>Bagaimana Ibu melakukan itu?</p>', '<p>Mengapa harus diberi pupuk, Bu?</p>', '<p>Apa yang sedang Ibu lakukan?</p>', '<p>Apa yang bias aku bantu, Bu?</p>', '', '', '', '2', '', '3', 'main');
