INSERT INTO `bank_soal` VALUES (428, 118, 0, '<p>Cermatilah puisi berikut!</p>\r\n<p><strong>Kupu-kupu</strong></p>\r\n<p>Alangkah elok warnamu</p>\r\n<p>Terbang di antara bunga-bunga</p>\r\n<p>Mencari madu</p>\r\n<p>Kadang kulihat engkau berayun</p>\r\n<p>Di tangkai dan daun-daun</p>\r\n<p>Atau berkejaran bersama kawananmu</p>\r\n<p>Kupu-kupu</p>\r\n<p>Alangkah senang aku melihatmu</p>\r\n<p>Dapatkah aku memiliki sayap endah</p>\r\n<p>Seperti sayapmu</p>\r\n<p>Karya Sigit B.K.</p>\r\n<p>Isi puisi tersebut adalah . . . .</p>', 'Simulasi US/M', '<p>Menggambarkan keindahan kupu-kupu</p>', '<p>Kupu-kupu yang mulai bertelur.</p>', '<p>Kupu-kupu yag suka mencari madu.</p>', '<p>Menggambarkan keindahan bunga-bunga di taman.</p>', '', '', '', '2', '', '1', 'main');
INSERT INTO `bank_soal` VALUES (429, 118, 0, '<p>Perhatikan cuplikan cerita anak-anak berikut!</p>\r\n<p> </p>\r\n<p>Si kancil berjalan berjingkat-jingkat di kebun Pak Tani. Kancil hendak mencuri mentimun. Di tengah-tengah ada boneka orang-orangan, Kancil tidak takut. Ia pun menendang boneka itu. Ternyata, boneka orang-orangan itu telah diberi getah nangka sehingga kaki Kancil menempel pada boneka itu dan sulit dilepaslan. Kemudian, Pak Tani datang menangkap Kancil.</p>\r\n<p> </p>\r\n<p>Kesimpulan dari paragraf tersebut adalah . . .</p>', 'Simulasi US/M', '<p>Si Kancil yang nakal dan tertangkap.</p>\r\n<p><strong> </strong></p>', '<p>Si Kancil yang suka mencuri dan terperangkap.</p>', '<p>Si Kancil yang sombong dan terperangkap</p>', '<p>Si Kancil yang malas dan terperangkap</p>', '', '', '', '2', '', '2', 'main');
INSERT INTO `bank_soal` VALUES (430, 118, 0, '<p><strong>Laporan Hasil Pengamatan Pelestaria Musik Angklung</strong></p>\r\n<p>Hari, tanggal         : Minggu, 8 Juni 2014</p>\r\n<p>Waktu                    : 08.00 s.d. selesai</p>\r\n<p>Tempat                  : Saung Mang Ujo</p>\r\n<p>Hasil Pengamatan :</p>\r\n<p>Seni angklung Mang Ujo merupakan hasil kerja kerasnya ketika belajar di sekolah kesenian Jawa Barat. karena kerja keras Mang Ujo, kesenian tradisional angklung menjadi terkenal di Indonesia. Bahkan, angklung juga dikenal masyarakat dunia. <em>Di Belanda, Saudi Arabia, Swiss, Jepang, dan Argentina, seni angklung pernah dipertunjukkan.</em></p>\r\n<p> </p>\r\n<p>Isi laporan tersebut adalah . . . .</p>', 'Simulasi US/M', '<p>Kerja keras Mang Ujo meciptakan angklung untuk dijual di Belanda, Saudi Arabia, Swiss, Jepang, dan Argentina.</p>', '<p>Kerja keras Mang Ujo membuat seni angklung terkenal di Indonesia dan dunia.</p>', '<p>Mang Ujo bekerja keras mempromosikan seni angklung di luar negeri.</p>', '<p>Perjuangan keras Mang Ujo memasarkan dan mempertunjukkan angklung hingga luar negeri.</p>', '', '', '', '2', '', '2', 'main');
INSERT INTO `bank_soal` VALUES (431, 118, 0, '<p><strong>Laporan Hasil Pengamatan Pelestaria Musik Angklung</strong></p>\r\n<p>Hari, tanggal         : Minggu, 8 Juni 2014</p>\r\n<p>Waktu                    : 08.00 s.d. selesai</p>\r\n<p>Tempat                  : Saung Mang Ujo</p>\r\n<p>Hasil Pengamatan :</p>\r\n<p>Seni angklung Mang Ujo merupakan hasil kerja kerasnya ketika belajar di sekolah kesenian Jawa Barat. karena kerja keras Mang Ujo, kesenian tradisional angklung menjadi terkenal di Indonesia. Bahkan, angklung juga dikenal masyarakat dunia. <em>Di Belanda, Saudi Arabia, Swiss, Jepang, dan Argentina, seni angklung pernah dipertunjukkan.</em></p>\r\n<p> </p>\r\n<p>Perbaikan penulisan yang dicetak miring pada laporan tersebut yang benar adalah . . . .</p>', 'Simulasi US/M', '<p>Belanda, Saudi Arabia, Swiss, Jepang, dan Argentina, seni angklung pernah dipertunjukkan.</p>', '<p>Seni angklung, di Belanda, Saudi Arabia, Swiss, Jepang, dan Argentina, pernah dipertunjukkan.</p>', '<p>Seni angklung pernah dipertunjukkan di Belanda, Saudi Arabia, Swiss, Jepang, dan Argentina.</p>', '<p>Pernah seni angklung dipertunjukkan di Belanda, Saudi Arabia, Swiss, Jepang, dan Argentina.</p>', '', '', '', '2', '', '3', 'main');
INSERT INTO `bank_soal` VALUES (432, 118, 0, '<p>Bacalah percakapan berikut!</p>\r\n<p>Yosi                : “Wan, ayo kita mincing di Sukun!”</p>\r\n<p>Wawan          : “<em>Lho,</em> kemarin, <em>kan</em>, sudah mincing?”</p>\r\n<p>Yosi                : “Iya. Tapi saya dengar, di Sukun ikannya besar-besar.”</p>\r\n<p>Wawan          : “Oh begitu. Bolehlah. Kapan berangkat?”</p>\r\n<p>Yosi                : “Nanti pulang sekolah, ya!”</p>\r\n<p> </p>\r\n<p>Topik percakapan di atas adalah . . . .</p>', 'Simulasi US/M', '<p>Ajakan memancing di Sukun</p>', '<p>Cerita pengalaman memancing</p>', '<p>Memberi semangat kepada teman</p>\r\n<p> </p>', '<p>Memberi tahu tempat memancing</p>', '', '', '', '2', '', '1', 'main');
INSERT INTO `bank_soal` VALUES (433, 118, 0, '<p>Dewasa ini pencemaran lingkungan di dunia sangat memprihatinkan. Para ahl berpendapat jika perusakan lingkungan masih terjadi maka keseimbangan alam akan terganggu. Pencemaran dan pengrusakan alam harus segera dihentikan, mengingat jika keseimbangan alam terganggu maka kehiduppan manusia juga akan terganggu.</p>\r\n<p> </p>\r\n<p>Kalimat ringkasan yang tepat berdasarkan paragraf tersebut adalah . . . .</p>', 'Simulasi US/M', '<p>Pencemaran dan kerusakan lingkungan perlu dihentikan untuk menjaga keseimbangan alam.</p>', '<p>Jangan menebang hutan sembarangan tanpa perhitungan.</p>', '<p>Kerusakan alam perlu diantisipasi melalui penghijauan.</p>', '<p>Manusia paling bertanggung jawab terhadap kerusakan lingkungan.</p>', '', '', '', '2', '', '1', 'main');
