INSERT INTO `bank_soal` VALUES (435, 118, 0, '<p>Dewasa ini pencemaran lingkungan di dunia sangat memprihatinkan. Para ahl berpendapat jika perusakan lingkungan masih terjadi maka keseimbangan alam akan terganggu. Pencemaran dan pengrusakan alam harus segera dihentikan, mengingat jika keseimbangan alam terganggu maka kehiduppan manusia juga akan terganggu.</p>\r\n<p> </p>\r\n<p>Pesan yang terkandung dalam paragraf tersebut adalah . . . .</p>', 'Simulasi US/M', '<p>Para ahli tidak perlu saling menyalahkan atas kerusakan lingkungan.</p>', '<p>Manusia harus menjaga lingkungan agar tetap terjaga keseimbangannya.</p>', '<p>Lingkungan yang seimbang sangat dibutuhkan dalam kehidupan masyarakat.</p>\r\n<p> </p>', '<p>Kesejahteraan manusia ditentukan lingkungannya.</p>', '', '', '', '2', '', '2', 'main');
INSERT INTO `bank_soal` VALUES (436, 118, 0, '<p><strong>Guru</strong></p>\r\n<p>Guruku . . .</p>\r\n<p>Kala mentari masih tertidur</p>\r\n<p>Kau terjang embun pagi</p>\r\n<p>Mengejar ketepatan waktu</p>\r\n<p>Memberi bekal ilmu anak didikmu</p>\r\n<p>Guruku . . .</p>\r\n<p>Sungguh besar pengabadianmu</p>\r\n<p>Jasa-jasamu yang luhur itu</p>\r\n<p>Tak akan kulupakan</p>\r\n<p>Sepanjang hayatku</p>\r\n<p> </p>\r\n<p> </p>\r\n<p>Pernyataan yang sesuai dengan puisi tersebut adalah . . . .</p>', 'Simulasi US/M', '<p>Kesungguhan anak dalam menuntut ilmu.</p>', '<p>Pujian atas jasa-jasa gurunya.</p>', '<p>Pengorbanan murid kepada guru yang dihormati.</p>', '<p>Perjuangan guru yang berangkat ke sekolah setiap hari.</p>', '', '', '', '2', '', '4', 'main');
INSERT INTO `bank_soal` VALUES (437, 118, 0, '<p><strong>Guru</strong></p>\r\n<p>Guruku . . .</p>\r\n<p>Kala mentari masih tertidur</p>\r\n<p>Kau terjang embun pagi</p>\r\n<p>Mengejar ketepatan waktu</p>\r\n<p>Memberi bekal ilmu anak didikmu</p>\r\n<p>Guruku . . .</p>\r\n<p>Sungguh besar pengabadianmu</p>\r\n<p>Jasa-jasamu yang luhur itu</p>\r\n<p>Tak akan kulupakan</p>\r\n<p>Sepanjang hayatku</p>\r\n<p> </p>\r\n<p> </p>\r\n<p>Makna kata <em>hayat</em> dalam puisi tersebut adalah . . . .</p>', 'Simulasi US/M', '<p>Waktu</p>', '<p>Hidup</p>', '<p>Perjalanan</p>', '<p>Nasib</p>', '', '', '', '2', '', '2', 'main');
INSERT INTO `bank_soal` VALUES (438, 118, 0, '<p> Bacalah petunjuk berikut!</p>\r\n<p><strong>Petunjuk Penggunaan Blender</strong></p>\r\n<p>1)    Tekan tombol on pada blender.</p>\r\n<p>2)    Masukkan buah yang sudah dipotong-potong.</p>\r\n<p>3)    Tekan tombol off pada blender apabila buah sudah halus.</p>\r\n<p>4)    Pastikan kabel blender terhubung dengan stopkontak.</p>\r\n<p>5)    Cabut kabel blender dari stopkontak setelah digunakan.</p>\r\n<p>Urutan petunjuk penggunaan blender yang tepat adalah . . . .</p>', 'Simulasi US/M', '<p>2-1-4-3-5</p>', '<p>2-4-1-3-5</p>', '<p>2-4-1-5-3</p>', '<p>2-5-1-3-4</p>', '', '', '', '2', '', '4', 'main');
INSERT INTO `bank_soal` VALUES (439, 118, 0, '<p>Bacalah kalimat acak berikut!</p>\r\n<p>1)    Tiba-tiba gigiku terasa mau tanggal</p>\r\n<p>2)    Ups, gigiku terpental dan jatuh</p>\r\n<p>3)    Benar juga, setelah aku dorong dengan lidah.</p>\r\n<p>4)    Hari itu aku sedang makan salak pondoh.</p>\r\n<p>Kalimat tersebut akan menjadi paragraf padu bila disusun dari nomor . . . .</p>', 'Simulasi US/M', '<p>1-4-3-2</p>', '<p>2-1-4-3</p>', '<p>3-4-2-1</p>', '<p>4-1-3-2</p>', '', '<p>Urutan kalimat yang padu adalah</p>\r\n<p>(3) – (4) – (1) – (2)</p>', '', '2', '', '2', 'main');
INSERT INTO `bank_soal` VALUES (440, 118, 0, '<p>Ria tidak masuk sekolah.</p>\r\n<p>Semalam suhu tubuhnya tinggi.</p>\r\n<p>Gabungan kedua kalimat di atas yang benar adalah . . . .</p>', 'Simulasi US/M', '<p>Ria tidak masuk sekolah karena semalam suhu tubuhnya tinggi</p>', '<p>Ria tidak masuk sekolah sehingga semalam suhu tubuhnya tinggi</p>', '<p>Ria tidak masuk sekolah walaupun semalam suhu tubuhnya tinggi</p>', '<p>Ria tidak masuk sekolah, tetapi semalam suhu tubuhnya tinggi</p>', '', '', '', '2', '', '1', 'main');
INSERT INTO `bank_soal` VALUES (441, 118, 0, '<p>Yosi menjadi anak yang mandiri dan berprestasi . . . semenjak lahir ia sudah ditinggal kedua orang tuanya.</p>\r\n<p>Kata hubung yang tepat untuk melengkapi kalimat tersebut adalah . . . .</p>', 'Simulasi US/M', '<p>Sehingga</p>', '<p>Dari</p>', '<p>Walaupun</p>', '<p>Supaya</p>', '', '', '', '2', '', '3', 'main');
