INSERT INTO `bank_soal` VALUES (444, 118, 0, '<p>Dalam rangka Peringatan Bulan Bahasa yang diberi nama Olimpiade Sastra Indonesia (OSI), SD Mekar Sari mengadakan berbagai jenis perlombaan pada tanggal 24, 25, dan 26 Oktober 2013. Lomba tersebut, antara lain membuat pantun, laporan/resensi buku, menulis cerita berdasarkan gambar, synopsis/ menceritakan kembali isi bacaan, membaca puisi, membaca teknis, dan menulis sambung. Semua siswa harus mengikuti Janis lomba sesuai pilihan masing-masing.</p>\r\n<p> </p>\r\n<p>Isi cuplikan pengumuman di atas adalah</p>', 'Simulasi US/M', '<p>Berbagai lomba didikuti seluruh siswa SD Mekar Sari dalam rangka Bulan Bahasa</p>', '<p>Lomba antar kelas SD Mekar Sari dalam rangka memperingati Bulan Bahasa</p>', '<p>Lomba membaca dan menuli yang harus diikuti seluruh siswa SD Mekar Sari</p>', '<p>Lomba yang akan memperkuat rasa nasionalisme siswa SD Mekar Sari</p>\r\n<p> </p>', '', '', '', '2', '', '2', 'main');
INSERT INTO `bank_soal` VALUES (445, 118, 0, '<p><strong>Bacalah dialog berikut!</strong></p>\r\n<p>Pembeli          : “Pak berapa harga semangka ini?”</p>\r\n<p>Pedagang      : “Rp 15.000,00 per buah, Bu.”</p>\r\n<p>Pembeli          : “Ah, mahal sekali. Boleh kurang, ya?”</p>\r\n<p>Pedagang      : “. . .”</p>\r\n<p>Pembeli          : “Yah, sedikit saja, Pak.”</p>\r\n<p>Pedagang      : “Baiklah, Rp 14.000,00, ya, Bu?”</p>\r\n<p>Pembeli          : “Baiklah, ini uangnya, Pak.”</p>\r\n<p>Pedagang      : “Terima kasih, Bu.”</p>\r\n<p> </p>\r\n<p>Kalimat yang tepat untuk melengkapi dialog tersebut adalah . . . .</p>', 'Simulasi US/M', '<p>Maaf, Bu, boleh-boleh saja kalau kurang.</p>', '<p>Maaf, Bu, tidak bias. Nanti saya rugi.</p>', '<p>Oh, tentu saja, dengan senang hati.</p>\r\n<p> </p>', '<p>Jangan sembarangan menurunkan harga, Bu.</p>', '', '', '', '2', '', '2', 'main');
