INSERT INTO `bank_soal` VALUES (495, 118, 0, '<p><strong>Bacalah dialog berikut!</strong></p>\r\n<p>Pak Jaya       : “Anak-anak, mari kita hidupkan komputer masing-masing.”</p>\r\n<p>Doni               : “Maaf, Pak, komputer saya tidak bisa nyala.”</p>\r\n<p>Pak Jaya       : “Nyalakan dulu stopkontaknya, Don!”</p>\r\n<p>Doni               : “O iya, Pak. …”</p>\r\n<p> </p>\r\n<p>Kata yang tepat untuk melengkapi dialog tersebut adalah . . . .</p>', 'Simulasi US/M (2)', '<p>Terima kasih</p>', '<p>Hormat saya</p>', '<p>Hormat kami</p>', '<p>Maaf</p>', '', '', '', '2', '', '1', 'main');
