INSERT INTO `bank_soal` VALUES (544, 118, 0, '<p><strong>Perhatikan pengumuman berikut!</strong></p>\r\n<p><strong>PENGUMUMAN</strong></p>\r\n<p>(1) seluruh warga Baturejo diharapkan hadir dalam acara gotong-royong membuat pompa air pada hari Minggu, 30 Nevember 2014 di balai desa.</p>\r\n<p>(2) Pompa tersebut akan digunakan untuk memenuhi kebutuhan air di musim kemarau. Harap hadir tepat waktu.</p>\r\n<p>(3) Sekian, terima kasih.</p>\r\n<p> </p>\r\n<p>(4) Hormat kami</p>\r\n<p>Kepala Desa</p>\r\n<p> </p>\r\n<p> </p>\r\n<p>Muhlisin</p>\r\n<p>Bagian isi pengumuman tersebut ditunjukkan oleh nomor . . . .</p>', 'Simulasi US/M (3) ', '<p>1 dan 3</p>', '<p>1 dan 2</p>', '<p>3 dan 4</p>', '<p>4 dan 1</p>', '', '', '', '2', '', '2', 'main');
INSERT INTO `bank_soal` VALUES (545, 118, 0, '<p><strong>Bacalah dialog berikut!</strong></p>\r\n<p>Ibu                  : “Nadia jangan banyak cerita dulu, kakekmu masih capek.”</p>\r\n<p>Kakek            : “Tidak apa-apa. Nadia kan kangen sama kakek. Ya?”</p>\r\n<p>Nadia             : “. . .”</p>\r\n<p>Kakek            : “Oh, iya ini ada oleh-oleh. Bukalah, Nad!</p>\r\n<p> </p>\r\n<p>Kalimat yang tepat untuk melengkapi dialog tersebut adalah . . . .</p>', 'Simulasi US/M (3) ', '<p>Tentu, Kek! Kan sudah lama Nadia tidak katemu Kakek!</p>', '<p>Ya, Kek. Lebih baik saya istirahat saja!</p>', '<p>Bagaimana kalau saya minta oleh-olehnya dulu?</p>', '<p>Ibu ini kok melarang saya?</p>', '', '', '', '2', '', '1', 'main');
