INSERT INTO `bank_soal` VALUES (667, 41, 0, '<p><strong>Perhatikan puisi berikut!</strong></p>\r\n<p xss=removed><strong>PEJUANG</strong></p>\r\n<p xss=removed>Dengan semangat kau berjuang</p>\r\n<p xss=removed>Melawan pejajah</p>\r\n<p xss=removed>Tanpa rasa takut, kau perjuangkan negerimu untuk merdeka.</p>\r\n<p xss=removed>Engkau bersumpah walau harus mati di tangan penjajah</p>\r\n<p xss=removed>Pahlawanku ...</p>\r\n<p xss=removed>Engkau berhasil untuk memerdekakan negerimu,</p>\r\n<p xss=removed>Negeri Indonesia</p>\r\n<p xss=removed>Rakyatpun bersorak untuk dirimu</p>\r\n<p xss=removed>Merdeka ... merdeka ...</p>\r\n<p xss=removed>Pahlawanku ...</p>\r\n<p xss=removed>Terima kasih ...</p>\r\n<p>Tema kutipan puisi tersebut adalah . . . .</p>', 'Try Out 3', '<p>Penuntasan kemiskinan</p>', '<p>Damaikan peperangan</p>', '<p>Pahlawan pendidikan</p>', '<p>Pahlawan kemerdekaan</p>', '', '', '', '2', '', '4', 'main');
