INSERT INTO `bank_soal` VALUES (669, 41, 0, '<p><strong>Perhatikan kedua kutipan puisi berikut!</strong></p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td width=\"293\">\r\n<p><strong>Puisi I</strong></p>\r\n</td>\r\n<td width=\"293\">\r\n<p><strong>Puisi II</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"293\">\r\n<p>Pemuda untuk Perubahan</p>\r\n<p> </p>\r\n<p>Indonesia menangis</p>\r\n<p>Bahkan tercabik</p>\r\n<p>Dengan hebatnya penguasa korupsi</p>\r\n<p>Tak peduli rakyat menangis</p>\r\n<p>Dan bahkan menjadi pengemis</p>\r\n</td>\r\n<td width=\"293\">\r\n<p>Negeriku Tercinta Indonesia</p>\r\n<p> </p>\r\n<p>Nasibmu ini menderita</p>\r\n<p>Rakyatmu kini sengsara</p>\r\n<p>Pemimpin yang tidak bijaksana</p>\r\n<p>Apakah pantas memimpin negeri</p>\r\n<p>Yang aman sentosa</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>\r\n<p>Perbedaan amanat kedua puisi tersebut adalah . . . .</p>', 'Try Out 3', '<table width=\"584\">\r\n<tbody>\r\n<tr>\r\n<td xss=removed width=\"265\">\r\n<p>Puisi I</p>\r\n</td>\r\n<td xss=removed width=\"290\">\r\n<p>Puisi II</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"265\">\r\n<p>Jadilah rakyat kecil yang menangis kelaparan!</p>\r\n</td>\r\n<td width=\"290\">\r\n<p>Jangan biarkan pemimpin tidak bijaksana!</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '<table width=\"584\">\r\n<tbody>\r\n<tr>\r\n<td xss=removed width=\"265\">\r\n<p>Puisi I</p>\r\n</td>\r\n<td xss=removed width=\"290\">\r\n<p>Puisi II</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"265\">\r\n<p>Jadilah penguasa yang menyantuni pengemis!</p>\r\n</td>\r\n<td width=\"290\">\r\n<p>Pemimpin yang tidak bijaksana jangan dipaksa.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '<table width=\"584\">\r\n<tbody>\r\n<tr>\r\n<td xss=removed width=\"265\">\r\n<p>Puisi I</p>\r\n</td>\r\n<td xss=removed width=\"290\">\r\n<p>Puisi II</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"265\">\r\n<p>Ingatlah kepada rakyat yang menangis!</p>\r\n</td>\r\n<td width=\"290\">\r\n<p>Buatlah rakyat Indones